    TITLE
    , CONTENTS
    , WRITER
    , CREATE_DATE
    , UPDATE_DATE
) VALUES (
    #{title}
    , #{contents}
    , #{writer}
    , NOW()
    , NOW()
)

mapper.update:
UPDATE BOARD
SET
    TITLE           = #{title}
    , CONTENTS      = #{contents}
    , WRITER        = #{writer}
    , CREATE_DATE   = NOW()
    , UPDATE_DATE   = NOW()
WHERE NO            = #{no}
